import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom'
import ReadPosts from './pages/ReadPosts'
import CreateCrewmate from './pages/CreateCrewmate'
import EditPost from './pages/EditPost'
import { Link } from 'react-router-dom'

import {useState} from 'react';
import {supabase} from '../src/client'


const App = () => {
  
  const[crewmate, setCrewmate] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const {data} = await supabase.from('Crewmate')
      .select()
      .order('created_at', {ascending: true})
      setCrewmate(crewmate)
    }
    fetchData()
  })

  // Sets up routes
  let element = useRoutes([
    {
      path: "/",
      element:<ReadPosts data={crewmate}/>
    },
    {
      path:"/edit/:id",
      element: <EditPost data={crewmate} />
    },
    {
      path:"/new",
      element: <CreateCrewmate />
    }
  ]);

  return ( 

    <div className="App">
      <div className="header">
        <h1>CrewMates</h1>
        <Link to="/"><button> Crewmate Gallery  </button></Link>
        <Link to="/new"><button> Create a new crewmate! </button></Link>
      </div>
        {element}
    </div>

  );
}

export default App;
