import React from 'react'
import { useState } from 'react'
import './Card.css'
import { Link } from 'react-router-dom'


const Card = (props) =>  {

  const [count, setCount] = useState(0)
  const updateCount = () => {
    setCount((count) => count + 1);
  }

  return (
      <div className="Card">
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" /></Link>
          <h2 className="name">{props.title}</h2>
          <h3 className="speed">{"by " + props.speed}</h3>
          <p className="candy">{props.candy}</p>
          <button className="edit" onClick={editCrewmate} >Edit</button>
          <button className="delete" onClick={deleteCrewmate} >Delete</button>

      </div>
  );
};

export default Card;
